        <div className='left'>
          <form>
            <label for="post">Escribe tu mensaje aquí</label>
            <br></br>
            <textarea name="post" id="input" cols="30" rows={10}></textarea>
            <br></br> 
          </form>
        </div>
        <div className='right'></div>   
      </div>


    </div>
  );
}

export default App;
