import './App.css';

function App() {

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log("botón clickeado")

  };

  let message = "";

    const formValidation = (e) => {
      if (e.target.value === "") {
        message = "Este campo no puede estar vacío";
        console.log("error")
      } else {
        console.log("¡bien!")
      }
    }
    ///MANEJO DE LOS DATOS
    let data = {};


  return (
    <div className="App">
      <h1>Aplicación de mensajes</h1>
      <div className='container'>

        <div className='left'>
          <form onSubmit={(e) => handleSubmit(e)}>
            <label for="post">Escribe tu mensaje aquí</label>
            <br></br>
            <textarea name="post" id="input" cols="30" rows={10} onChange={(e)=> formValidation(e)}></textarea>
            <br></br>
            <div id="img">{message}</div>
            <button type="submit">Enviar</button>
          </form>
        </div>

        <div className='right'>
          <h3>Aquí están tus mensajes:</h3>
          <div id="post">
            <div>
              <p>Hola a todos! Este es el primer post</p>
              <span class="options">
                <i class="fas fa-edit"></i>
                <i class="fas fa-trash-alt"></i>
              </span>
            </div>

            <div >
              <p>Hola! post 2</p>
              <span class="options">
                <i class="fas fa-edit"></i>
                <i class="fas fa-trash-alt"></i>
              </span>
            </div>
          </div>
        </div>
      </div>

    </div>
  );
}

export default App;
