import './App.css';
import React, {useState} from "react";

function App() {
  const [message, setMessage] = useState(""); //Hook que controla el mensaje en caso de campo vacío
  const [data, setData] = useState({});
  let value = ""; //variable que guarda el valor del textarea


  const handleSubmit = (e) => { //función que controla el submit del formulario
    e.preventDefault();
    if(value === ""){
      setMessage("Debes ingresar texto en este campo");
      console.log(message)
    } else {
      setMessage("");
      acceptData();
    }
  };

  const formValidation = (e) => { //función que controla el campo en el textarea
    value = e.target.value;
    //console.log(value)
  }
  ///MANEJO DEL TEXTAREA

  const acceptData = ()=> {
    setData(value);
    console.log(data);
  }


  return (
    <div className="App">
      <h1>Aplicación de mensajes</h1>
      <div className='container'>

        <div className='left'>
          <form onSubmit={(e) => handleSubmit(e)}>
            <label for="post">Escribe tu mensaje aquí</label>
            <br></br>
            <textarea name="post" id="input" cols="30" rows={10} onChange={(e) => formValidation(e)}></textarea>
            <br></br>
            <div id="img">{message}</div>
            <button type="submit">Enviar</button>
          </form>
        </div>

        <div className='right'>
          <h3>Aquí están los mensajes:</h3>
          <div id="post">
            <div>
              <p>{data.text}</p>
              <span class="options">
                <i class="fas fa-edit"></i>
                <i class="fas fa-trash-alt"></i>
              </span>
            </div>

            <div >
              <p>Hola! post 2</p>
              <span class="options">
                <i class="fas fa-edit"></i>
                <i class="fas fa-trash-alt"></i>
              </span>
            </div>
          </div>
        </div>
      </div>

    </div>
  );
}

export default App;
