import './App.css';
import React, { useState, useEffect} from "react";

function App() {
  const [message, setMessage] = useState(""); //Hook que controla el mensaje en caso de campo vacío
  const [data, setData] = useState("");
  const [posts, setPosts] = useState([]);
  let value = ""; //variable que guarda el valor del textarea



  const handleSubmit = (e) => { //función que controla el submit del formulario
    e.preventDefault();
    if (value === "") {
      setMessage("Debes ingresar texto en este campo");
      console.log(message)
    } else {
      setMessage("");
      acceptData();
    }
  };

  const formValidation = (e) => { //función que controla el campo en el textarea
    value = e.target.value;
    setData(value);
    //console.log(value)
  }
  ///MANEJO DEL TEXTAREA

  const acceptData = () => {
    setData(value);
    console.log(data);
  }

  //FUNCION DE CONEXION CON LA API
  const bringMessages = (e) => {
    e.preventDefault();
    fetch('https://jsonplaceholder.typicode.com/posts?_start=0&_limit=5')
      .then((response) => response.json())
      .then((json) =>
        setPosts(json),
        console.log("los posts traidos de la API",posts));

  }



  return (
    <div className="App">
      <h1>Aplicación de mensajes</h1>
      <div className='container'>

        <div className='left'>
          <form onSubmit={(e) => handleSubmit(e)}>
            <label htmlFor="post">Escribe tu mensaje aquí</label>
            <br></br>
            <textarea name="post" id="input" cols="30" rows={5} onChange={(e) => formValidation(e)}></textarea>
            <br></br>
            <div id="img">{message}</div>
            <button type="submit">Enviar</button>
          </form>

          <button type="submit" onClick={(e) => bringMessages(e)}>Traer mensajes</button>
        </div>

        <div className='right'>
          <h3>Aquí están los mensajes:</h3>
          <div id="post">
            <div>        
              {!!posts && posts.length > 0 ?
                posts.map((post) => {
                return <p key={post.id}>{post.body}</p>
                })
                : ""
              }
            </div>

            <div >
              <p>Hola! post 2</p>
              <span className="options">
                <i className="fas fa-edit"></i>
                <i className="fas fa-trash-alt"></i>
              </span>
            </div>

          </div>
        </div>
      </div>

    </div>
  );
}

export default App;
